CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_refund_simpan` (`xKodeRefund` VARCHAR(50), `xKodeBooking` VARCHAR(50), `xTanggalRefund` DATE, `xAlasan` TEXT, `xTotal` DECIMAL(13,0), `xCompanyId` VARCHAR(10), `xUserId` VARCHAR(50))  begin
	start transaction; 
		set @usertime = (select fn_usertime(xUserId, xCompanyId)); 
		set @KodeBookingLalu = (select kode_reservasi from refund where kode_refund=xKodeRefund and companyid=xCompanyId limit 1);
		
		update 	booking
		set 		canceled=0
		where 	kode_booking=@KodeBookingLalu and companyid=xCompanyId;
		
		if not exists(select kode_refund from refund where kode_refund=xKodeRefund and companyid=xCompanyId limit 1) then
			set @notransaksi = (select fn_kode_refund(xTanggalRefund, xCompanyId));
			
			insert into refund (kode_refund, kode_booking, tanggal_refund, alasan, total, user_id, created_at, companyid, usertime)
			values(@notransaksi, xKodeBooking, xTanggalRefund, xAlasan, xTotal, xUserId, now(), xCompanyId, @usertime);
		else
			update 	refund 
			set 		kode_reservasi=xKodeBooking, tanggal_refund=xTanggalRefund, alasan=xAlasan, 
						total=xTotal, user_id=xUserId, updated_at=now(), usertime=@usertime
			where 	kode_refund=xKodeRefund and companyid=xCompanyId;
		end if;
		
		update 	booking
		set 		canceled=1
		where 	kode_booking=xKodeBooking and companyid=xCompanyId;
	commit;
end$$

