CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_reservasipenalty_simpan` (IN `xKodeKey` VARCHAR(50), IN `xKodeReservasi` VARCHAR(30), IN `xPembayaranPenalty` DECIMAL(13,0), IN `xCompanyId` VARCHAR(10))  begin 
	start transaction;   
		set @Usertime = (select fn_usertime(xKodeKey, xCompanyId));  
		
		delete 
		from 		penalty 
		where 	kode_reservasi=xKodeReservasi and companyid=xCompanyId;
		
		insert into penalty (kode_reservasi, kode_item, keterangan, qty, denda, total, companyid, usertime)
		select 	kode_reservasi, kode_item, keterangan, qty, denda, total, companyid, usertime
		from 		penaltytmp
		where 	kode_key=xKodeKey and kode_reservasi=xKodeReservasi and companyid=xCompanyId;
		
		update 	reservasi, 
					(
						select 	kode_reservasi, sum(total) as total
						from 		penalty
						where 	kode_reservasi=xKodeReservasi and companyid=xCompanyId
					) 	as total_penalty 
		set 		reservasi.penalty=ifnull(total_penalty.total, 0),
					reservasi.pembayaran_penalty=ifnull(xPembayaranPenalty, 0),
					reservasi.sisa_pembayaran_penalty=ifnull(total_penalty.total, 0) - ifnull(xPembayaranPenalty, 0),
					reservasi.updated_at=now()
		where 	reservasi.kode_reservasi=xKodeReservasi and reservasi.companyid=xCompanyId and 
					reservasi.kode_reservasi=total_penalty.kode_reservasi;
	commit;
end$$

