CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_reservasi_simpan` (IN `xKodeKey` VARCHAR(50), IN `xKodeReservasi` VARCHAR(50), IN `xNoIdentitas` VARCHAR(30), IN `xKodeRoom` VARCHAR(10), IN `xKodePlatform` VARCHAR(10), IN `xNoReferensi` VARCHAR(100), IN `xNamaCP` VARCHAR(50), IN `xTeleponCP` VARCHAR(30), IN `xStatusLongtime` INT, IN `xCheckIn` DATETIME, IN `xCheckOut` DATETIME, IN `xLamaInap` INT, IN `xHargaSatuanRoom` DECIMAL(13,0), IN `xBiayaLain` DECIMAL(13,0), IN `xDiscRoom` DECIMAL(5,2), IN `xPPNRoom` DECIMAL(5,2), IN `xDiscLayanan` DECIMAL(5,2), IN `xPPNLayanan` DECIMAL(5,2), IN `xTotalPembayaran` DECIMAL(13,0), IN `xDeposit` DECIMAL(13,0), IN `xStatusCheckIn` INT, IN `xKeterangan` VARCHAR(100), IN `xCatatan` VARCHAR(100), IN `xAlasan` VARCHAR(100), IN `xCompanyId` VARCHAR(10))  begin 
	declare zRoomHarga Decimal(13,0) default 0;
	declare zRoomDiscRp Decimal(13,0) default 0;
	declare zRoomMinDisc Decimal(13,0) default 0;
	declare zRoomPPNRp Decimal(13,0) default 0;
	declare zRoomTotal Decimal(13,0) default 0; 

	declare zLayananSubTotal Decimal(13,0) default 0;
	declare zLayananDiscRp Decimal(13,0) default 0;
	declare zLayananMinDisc Decimal(13,0) default 0;
	declare zLayananPPNRp Decimal(13,0) default 0;
	declare zLayananTotal Decimal(13,0) default 0;
	
	declare zGrandTotal Decimal(13,0) default 0;
	declare zSisaPembayaran Decimal(13,0) default 0;
	
	set zRoomHarga = ifnull(xHargaSatuanRoom, 0) * ifnull(xLamaInap, 0);
	set zRoomDiscRp = round((ifnull(zRoomHarga, 0) * ifnull(xDiscRoom, 0)) / 100, 0);
	set zRoomMinDisc = ifnull(zRoomHarga, 0) - ifnull(zRoomDiscRp, 0);
	set zRoomPPNRp = round((ifnull(zRoomMinDisc, 0) * ifnull(xPPNRoom, 0)) / 100, 0);
	set zRoomTotal = ifnull(zRoomMinDisc, 0) + ifnull(zRoomPPNRp, 0);
	
	set zLayananSubTotal = (select 	ifnull(sum(total), 0) as total
							from 	reservasi_detailtmp 
							where 	kode_key=xKodeKey and kode_reservasi=xKodeReservasi and companyid=xCompanyId);
	set zLayananDiscRp = round((ifnull(zLayananSubTotal, 0) * ifnull(xDiscLayanan, 0)) / 100, 0);
	set zLayananMinDisc = ifnull(zLayananSubTotal, 0) - ifnull(zLayananDiscRp, 0);
	set zLayananPPNRp = round((ifnull(zLayananMinDisc, 0) * ifnull(xPPNLayanan, 0)) / 100, 0);
	set zLayananTotal = ifnull(zLayananMinDisc, 0) + ifnull(zLayananPPNRp, 0);
	
	set zGrandTotal = ifnull(zRoomTotal, 0) + ifnull(zLayananTotal, 0) + ifnull(xBiayaLain, 0);
	set zSisaPembayaran = ifnull(zGrandTotal, 0) - ifnull(xTotalPembayaran, 0);
		 
	start transaction; 		
		set @Usertime = (select fn_usertime(xKodeKey, xCompanyId));  
		
		delete
		from    reservasi_detail 
		where   kode_reservasi=xKodeReservasi and companyid=xCompanyId;
				
		delete 
		from 	reservasi_layanan 
		where 	kode_reservasi=xKodeReservasi and companyid=xCompanyId;
		
		delete 
		from 	deposit 
		where 	kode_reservasi=xKodeReservasi and ifnull(debit, 0) > 0 and companyid=xCompanyId;
			
		if not exists (select kode_reservasi from reservasi where kode_reservasi=xKodeReservasi and companyid=xCompanyId limit 1) then
			set @NoTransaksi = (select fn_kode_reservasi(date_format(now(), '%Y-%m-%d'), xCompanyId)); 
			
			insert into reservasi (kode_reservasi, tanggal_reservasi, no_identitas, kode_room, kode_platform, 
									no_referensi, nama_cp, telepon_cp, status_longtime, lama_inap, check_in, check_out, 
									harga_satuan_room, harga_room, disc, disc_rp, ppn, ppn_rp, total_room, total_layanan,
									biaya_lain, grand_total, total_pembayaran, sisa_pembayaran, deposit, 
									keterangan, catatan, alasan, user_id, created_at, companyid, usertime)
			values (@NoTransaksi, now(), if(xNoIdentitas <> '', xNoIdentitas, null), xKodeRoom, xKodePlatform, 
					xNoReferensi, xNamaCP, xTeleponCP, xStatusLongtime, ifnull(xLamaInap, 0), xCheckIn, xCheckOut, 
					ifnull(xHargaSatuanRoom, 0), ifnull(zRoomHarga, 0), ifnull(xDiscRoom, 0), ifnull(zRoomDiscRp, 0), 
					ifnull(xPPNRoom, 0), ifnull(zRoomPPNRp, 0), ifnull(zRoomTotal, 0), ifnull(zLayananTotal, 0), 
					ifnull(xBiayaLain, 0), ifnull(zGrandTotal, 0), ifnull(xTotalPembayaran, 0), ifnull(zSisaPembayaran, 0),
					ifnull(xDeposit, 0), xKeterangan, xCatatan, xAlasan, xKodeKey, now(), xCompanyId, @Usertime);
			
			insert into reservasi_detail (kode_reservasi, kode_layanan, harga, disc, disc_rp, qty, total, companyid, usertime)
			select 	@NoTransaksi, kode_layanan, ifnull(harga, 0), ifnull(disc, 0), 
					round((ifnull(harga, 0) * ifnull(disc, 0)) / 100, 0), ifnull(qty, 0), 
					(ifnull(harga, 0) * ifnull(qty, 0)) - round((((ifnull(harga, 0) * ifnull(qty, 0)) * ifnull(disc, 0)) / 100), 0), 
					companyid, usertime 
			from 		reservasi_detailtmp 
			where 	kode_key=xKodeKey and kode_reservasi=xKodeReservasi and companyid=xCompanyId;
				
			insert into reservasi_layanan (kode_reservasi, total, disc, disc_rp, ppn, ppn_rp, grand_total, companyid, usertime)
			values(@NoTransaksi, ifnull(zLayananSubTotal, 0), ifnull(xDiscLayanan, 0), ifnull(zLayananDiscRp, 0), ifnull(xPPNLayanan, 0),
					ifnull(zLayananPPNRp, 0), ifnull(zLayananTotal, 0), xCompanyId, @Usertime);
							
			if (ifnull(xDeposit, 0) > 0) then
				insert into deposit (kode_reservasi, tanggal, debit, kredit, companyid, usertime)
				values (@NoTransaksi, date_format(now(), '%Y-%m-%d'), ifnull(xDeposit, 0), 0, xCompanyId, @Usertime);
			end if; 
		else 
			insert into reservasi_detail (kode_reservasi, kode_layanan, harga, disc, disc_rp, qty, total, companyid, usertime)
			select 	xKodeReservasi, kode_layanan, ifnull(harga, 0), ifnull(disc, 0), 
					round((ifnull(harga, 0) * ifnull(disc, 0)) / 100, 0), ifnull(qty, 0), 
					(ifnull(harga, 0) * ifnull(qty, 0)) - round((((ifnull(harga, 0) * ifnull(qty, 0)) * ifnull(disc, 0)) / 100), 0), 
					companyid, usertime 
			from 		reservasi_detailtmp 
			where 	kode_key=xKodeKey and kode_reservasi=xKodeReservasi and companyid=xCompanyId;
				
			insert into reservasi_layanan (kode_reservasi, total, disc, disc_rp, ppn, ppn_rp, grand_total, companyid, usertime)
			values(xKodeReservasi, ifnull(zLayananSubTotal, 0), ifnull(xDiscLayanan, 0), ifnull(zLayananDiscRp, 0), ifnull(xPPNLayanan, 0),
					ifnull(zLayananPPNRp, 0), ifnull(zLayananTotal, 0), xCompanyId, @Usertime);
				
			if (ifnull(xDeposit, 0) > 0) then
				insert into deposit (kode_reservasi, tanggal, debit, kredit, companyid, usertime)
				values (xKodeReservasi, date_format(now(), '%Y-%m-%d'), ifnull(xDeposit, 0), 0, xCompanyId, @Usertime);
			end if; 
				 
			update 	reservasi 
			set 	no_identitas=if(xNoIdentitas <> '', xNoIdentitas, null), kode_room=xKodeRoom, 
					kode_platform=xKodePlatform, no_referensi=xNoReferensi, nama_cp=xNamaCP, 
					telepon_cp=xTeleponCP, status_longtime=xStatusLongtime, lama_inap=ifnull(xLamaInap, 0), 
					check_in=xCheckIn, check_out=xCheckOut, harga_satuan_room=ifnull(xHargaSatuanRoom, 0),
					harga_room=ifnull(zRoomHarga, 0), disc=ifnull(xDiscRoom, 0), disc_rp=ifnull(zRoomDiscRp, 0), 
					ppn=ifnull(xPPNRoom, 0), ppn_rp=ifnull(zRoomPPNRp, 0), total_room=ifnull(zRoomTotal, 0),
					total_layanan=ifnull(zLayananTotal, 0), biaya_lain=ifnull(xBiayaLain, 0), 
					grand_total=ifnull(zGrandTotal, 0), total_pembayaran=ifnull(xTotalPembayaran, 0),
					sisa_pembayaran=ifnull(zSisaPembayaran, 0), deposit=ifnull(xDeposit, 0), 
					keterangan=xKeterangan, catatan=xCatatan, alasan=xAlasan, user_id=xKodeKey, 
					updated_at=now(), usertime=@Usertime
			where 	kode_reservasi=xKodeReservasi and companyid=xCompanyId; 
		end if;
		
		if(xStatusCheckIn = 1) then
			update 	reservasi 
			set 	status_in=1, tanggal_in=now()
			where 	kode_reservasi=xKodeReservasi and companyid=xCompanyId;
		end if; 
		
		delete
		from	reservasi_detailtmp 
		where 	kode_key=xKodeKey and companyid=xCompanyId;
	commit;
end$$

