CREATE DEFINER=`root`@`localhost` FUNCTION `fn_angka_romawi` (`xNumber` INT) RETURNS TEXT CHARSET latin1 begin 
	declare basic_roman text default 'M,CM,D,CD,C,XC,L,XL,X,IX,V,IV,I';
	declare basic_value text default '1000,900,500,400,100,90,50,40,10,9,5,4,1';
	declare roman_string text default '';
	declare i int default 1;
	declare roman_symbol text;
	declare roman_value int;
 
	set roman_string = (select if(xNumber<=0 or xNumber>=4000,null,roman_string));
 
	while xNumber > 0 and xNumber < 4000 do
 
		set roman_symbol = substring_index(substring_index(basic_roman,',',i),',',-1);
		set roman_value  = substring_index(substring_index(basic_value,',',i),',',-1);
 
		if xNumber >= roman_value then
			set roman_string = concat(roman_string,roman_symbol);
			set xNumber = xNumber - roman_value;
		else
			set i = i + 1;
		end if;
 
	end while;
 
	return 	roman_string;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `fn_kode_booking` (`xTanggal` DATE, `xCompanyId` VARCHAR(10)) RETURNS TEXT CHARSET latin1 begin
	declare xInisialKode varchar(6); 
	declare xNoTerakhir integer default 0;
	declare xUrutan varchar(3);
	declare xKode varchar(30);
	
	set xInisialKode = (select ltrim(rtrim(upper(kode))) from kode where menu='BOOKING' limit 1);
	set xNoTerakhir = (select 	ifnull(max(left(kode_booking, 3)), 0) 
								from 		booking 
								where 	month(tanggal_booking)=month(xTanggal) and 
											year(tanggal_booking)=year(xTanggal) and 
											companyid=xCompanyId
								order by kode_booking desc 
								limit 1);
	set xUrutan = right(concat('000', xNoTerakhir + 1), 3);
	
	set xKode = concat(xUrutan, '/', ltrim(rtrim(xInisialKode)), '/', ltrim(rtrim(upper(xCompanyId))), '/', fn_angka_romawi(month(xTanggal)), '/', year(xTanggal));
	
	return xKode;
end$$

